                                            className="fitBox inputField"
                                            onInvalid={I_update.exit}
                                            onChange={(x) =>
                                              setI_All(x.target.value)
                                            }
                                          >
                                            <option
                                              key={val.Quality}
                                              value={val.Quality}
                                              disabled
                                              selected="selected"
                                            >
                                              {val.I_Con}
                                            </option>
                                            {Qual_List.map((val, key) => (
                                              <option
                                                key={val.Quality}
                                                value={val.Quality}
                                              >
                                                {val.I_Con}
                                              </option>
                                            ))}
                                          </select>
                                        </td>
                                      </tr>
                                      <tr className="techSpecRow">
                                        <td className="techSpecTD1">
                                          <b>Fragile:</b>
                                        </td>
                                        <td className="techSpecTD1">
                                          <select
                                            id="ItemF"
                                            className="fitBox inputField"
                                            onInvalid={I_update.exit}
                                            onChange={(x) =>
                                              setI_Frag(x.target.value)
                                            }
                                          >
                                            <option
                                              key={val.Fragile}
                                              value={val.Fragile}
                                              disabled
                                              selected="selected"
                                            >
                                              {val.YesNo}
                                            </option>
                                            {Frag_List.map((val, key) => (
                                              <option
                                                key={val.Fragile}
                                                value={val.Fragile}
                                              >
                                                {val.YesNo}
                                              </option>
                                            ))}
                                          </select>
                                        </td>
                                      </tr>
                                      <tr className="techSpecRow">
                                        <td className="techSpecTD1">
                                          <b>Quantity:</b>
                                        </td>
                                        <td className="techSpecTD1">
                                          <div style={{ height: "29px" }}>
                                            <input
                                              type="text"
                                              className="inputField"
                                              id="ItemQ"
                                              defaultValue={val.Quantity}
                                              placeholder="Item Quantity"
                                              autocomplete="off"
                                              required
                                              onInvalid={I_update.exit}
                                              onChange={(x) =>
                                                setI_Num(x.target.value)
                                              }
                                            />
                                          </div>
                                        </td>
                                      </tr>
                                    </tbody>
                                  </table>
                                  <hr className="soft" />
                                  <button
                                    type="submit"
                                    className="btn swappor"
                                    onClick={I_update}
                                  >
                                    <i className="fa fa fa-edit" />
                                     Save detail
                                  </button>
                                  <img
                                    style={{ width: "180px", height: "50px" }}
                                    src={blank}
                                    alt=""
                                  />
                                  <button
                                    type="submit"
                                    id="del_but"
                                    className="btn swapper"
                                    onClick={I_delete}
                                  >
                                    <i className="fa fa-trash-o" />
                                     Delete item
                                  </button>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </form>
                </li>
              );
            })}
          </ul>
        </section>
        <footer>
          <p>
            Copyright © 2020 | Phlad Rian, The School Equipment Donation Website
          </p>
        </footer>
      </div>
    </div>
  );
}

export default withRouter(D_Add);
