/* eslint-disable */
import React, {useState} from "react";

export const Pagination = ({ Page_AllPost, TotalPost, Current_Page, paginate }) => {
  const pageNum = [];

  function prevClick() {
    if (Current_Page > 1) {
      paginate(Current_Page - 1);
    }
  }

  function nextClick() {
    if (Current_Page == TotalPost) {
      document.getElementById("next").disabled = true;
    }
    if (Current_Page < TotalPost) {
      document.getElementById("next").disabled = false;
    }
  }

  for (let i = 1; i <= Math.ceil(TotalPost / Page_AllPost); i++) {
    pageNum.push(i);
  }

  return (
    <nav>
      <ul className="pagination">
        <div className="col-sm-1">
          <li id="prev">
            <a onClick={() => {prevClick();}}>«</a>
          </li>
        </div>
        {pageNum.map((number) => (
          <div className="col-sm-1">
            <li key={number} id={number} className={(Current_Page === number ? 'active' : '')}>
              <a onClick={() => paginate(number)}>{number}</a>
            </li>
          </div>
        ))}
        <div className="col-sm-1">
          <li id="next">
            <a onClick={() => paginate(Current_Page + 1)}>»</a>
          </li>
        </div>
      </ul>
    </nav>
  );
};

export default Pagination;
