/* eslint-disable */
import React from "react";

function changeclass() {
  $('.pagBut').click(function() {
    $(this).removeClass("col-sm-1");
    $(this).addClass("col-sm-1 active");
  });
} 

export const Pagination = ({ Page_AllPost, TotalPost, paginate }) => {
  const pageNum = [];
  for (let i = 1; i <= Math.ceil(TotalPost / Page_AllPost); i++) {
    pageNum.push(i);
  }
  return (
    <nav>
      <ul className="pagination">
        <div className="col-sm-1">
          <li>
            <a href>«</a>
          </li>
        </div>
        {pageNum.map((number) => (
          <div id="pagBut" className="col-sm-1" onClick={changeclass}>
            <li key={number}>
              <a onClick={() => paginate(number)}>{number}</a>
            </li>
          </div>
        ))}
        <div className="col-sm-1">
          <li>
            <a href>»</a>
          </li>
        </div>
      </ul>
    </nav>
  );
};

export default Pagination;
