/* eslint-disable */
import React, {useState} from "react";

export const Pagination = ({ Page_AllPost, TotalPost, Current_Page, paginate }) => {
  const pageNum = [];

  for (let i = 1; i <= Math.ceil(TotalPost / Page_AllPost); i++) {
    pageNum.push(i);
  }
  return (
    <div>
    {pageNum.map((number) => (
    <nav>
      <ul className="pagination">
        <div className="col-sm-1">
          <li>
            <a>«</a>
          </li>
        </div>
        {pageNum.map((number) => (
          <div className="col-sm-1">
            <li key={number} id={number} className={(Current_Page === number ? 'active' : '')}>
              <a onClick={() => paginate(number)}>{number}</a>
            </li>
          </div>
        ))}
        <div className="col-sm-1">
          <li>
            <a>»</a>
          </li>
        </div>
      </ul>
    </nav>
    ))}
    </div>
  );
};

export default Pagination;
