/* eslint-disable */
import React, {useState} from "react";

export const Pagination = ({ Page_AllPost, TotalPost, Current_Page, paginate, ItemCount }) => {
  const pageNum = [];
  const [minPageNumberLimit, setminPageNumberLimit] = useState(0);
  const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);

  function prevClick() {
    if (Current_Page > 1) {
      paginate(Current_Page - 1);
    }
  }

  function nextClick() {
    if (Current_Page < ItemCount) {
      paginate(Current_Page + 1);
    }
  }

  for (let i = 1; i <= Math.ceil(TotalPost / Page_AllPost); i++) {
    pageNum.push(i);
  }

  return (
    <nav>
      <ul className="pagination">
        <div className="col-sm-1">
          <li id="prev">
            <a onClick={() => {prevClick();}}>«</a>
          </li>
        </div>
        {pageNum.map((number) => {
          <div className="col-sm-1">
            <li key={number} id={number} className={(Current_Page === number ? 'active' : '')}>
              <a onClick={() => paginate(number)}>{number}</a>
            </li>
          </div>
        })}
        <div className="col-sm-1">
          <li id="next">
            <a onClick={() => {nextClick();}}>»</a>
          </li>
        </div>
      </ul>
    </nav>
  );
};

export default Pagination;
