                  type="text"
                  className="form-control"
                  id="EmailR"
                  placeholder="Email Address"
                  autocomplete="off"
                  onInvalid={R_regis.exit}
                  required
                  onChange={(x) => setR_emailReg(x.target.value)}
                />
                <input
                  type="text"
                  className="form-control"
                  id="PhoneR"
                  placeholder="Tel Number"
                  autocomplete="off"
                  onInvalid={R_regis.exit}
                  required
                  onChange={(x) => setR_phoneReg(x.target.value)}
