                />
                <input
                  type="password"
                  className="form-control"
                  id="PasswordR"
                  placeholder="Password"
                  autocomplete="off"
                  onInvalid={R_regis.exit}
                  required
                  onChange={(x) => setR_passReg(x.target.value)}
                />
                <input
                  type="password"
                  className="form-control"
                  id="C_passwordR"
                  placeholder="Confirm Password"
                  autocomplete="off"
                  onInvalid={R_regis.exit}
                  required
                />
                <br />
                <input
                  type="submit"
                  className="form-control"
                  onClick={R_regis}
                />
              </form>
              <p>Thank you for your visiting!</p>
            </div>
          </div>
        </div>
        <div
          className="modal fade"
          id="modal3"
          tabIndex={-1}
          role="dialog"
          aria-labelledby="myModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog">
            <div className="modal-content modal-popup">
              <div className="modal-header">
                <button
                  type="button"
                  className="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">×</span>
                </button>
                <br />

                {/* Donor */}
                <h2 className="modal-title">Donor Registration</h2>
              </div>
              <form onSubmit={handleSubmit1}>
                <input
                  type="text"
                  className="form-control"
                  id="UsernameD"
                  placeholder="Username"
                  autocomplete="off"
                  onInvalid={D_regis.exit}
                  required
                  onChange={(y) => setD_nameReg(y.target.value)}
                />
                <input
                  type="text"
                  className="form-control"
                  id="EmailD"
                  placeholder="Email Address"
                  autocomplete="off"
                  onInvalid={D_regis.exit}
                  required
                  onChange={(y) => setD_emailReg(y.target.value)}
                />
                <input
                  type="text"
                  className="form-control"
                  id="PhoneD"
                  placeholder="Tel Number"
                  autocomplete="off"
                  onInvalid={D_regis.exit}
                  required
                  onChange={(y) => setD_phoneReg(y.target.value)}
                />
                <textarea
                  type="text"
                  className="form-control"
                  rows="4"
                  id="AddressD"
                  placeholder="House Location"
                  autocomplete="off"
                  onInvalid={D_regis.exit}
                  required
                  onChange={(y) => setD_localReg(y.target.value)}
                />
                <input
                  type="password"
                  className="form-control"
                  id="PasswordD"
                  placeholder="Password"
                  autocomplete="off"
                  onInvalid={D_regis.exit}
                  required
                  onChange={(y) => setD_passReg(y.target.value)}
                />
                <input
                  type="password"
                  className="form-control"
                  id="C_passwordD"
                  placeholder="Confirm Password"
                  autocomplete="off"
                  onInvalid={D_regis.exit}
                  required
                />
                <br />
                <input
                  type="submit"
                  className="form-control"
                  onClick={D_regis}
                />
              </form>
              <p>Thank you for your visiting!</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default withRouter(Authen);
