import React, {useState} from "react";

export const Pagination = ({ Page_AllPost, TotalPost, Current_Page, paginate, ItemCount }) => {
  const pageNum = [];
  const [minPageNumberLimit, setminPageNumberLimit] = useState(0);
  const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);

  function prevClick() {
    if (Current_Page > 1) {
      paginate(Current_Page - 1);
    }
  }

  function nextClick() {
    if (Current_Page < ItemCount) {
      paginate(Current_Page + 1);
    }
  }

  let pageDecrementBtn = null;
  if (minPageNumberLimit >= 1) {
    pageDecrementBtn = <div className="col-sm-1">
          <li>
            <a>...</a>
          </li>
        </div>;
  }

   let pageIncrementBtn = null;
  if (ItemCount > maxPageNumberLimit) {
    pageIncrementBtn =         <div className="col-sm-1">
          <li>
            <a>...</a>
          </li>
        </div>;
  }

  for (let i = 1; i <= Math.ceil(TotalPost / Page_AllPost); i++) {
    pageNum.push(i);
  }

  return (
    <nav>
      <ul className="pagination">
        <div className="col-sm-1">
          <li id="prev">
            <a onClick={() => {prevClick();}}>Â«</a>
          </li>
        </div>
        {pageDecrementBtn}
        {pageNum.map((number) => {
if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
      return (
        <div className="col-sm-1">
            <li key={number} id={number} className={(Current_Page === number ? 'active' : '')}>
              <a onClick={() => paginate(number)}>{number}</a>
            </li>
          </div>
      );
    } else {
      return null;
    }
        })}
        {pageIncrementBtn}
        <div className="col-sm-1">
          <li id="next">
            <a onClick={() => {nextClick();}}>Â»</a>
          </li>
        </div>
      </ul>
    </nav>
  );
};

export default Pagination;
